function updateCounter(){var e,t=document.getElementById("start-date").value;t&&(t=new Date(t),e=new Date,e=Math.abs(e-t),t=Math.ceil(e/864e5),document.getElementById("counter").innerText=t)}window.onload=()=>{var e=new Date;e.setDate(e.getDate()-30),document.getElementById("start-date").value=e.toISOString().split("T")[0],updateCounter()};